INSERT INTO `brgsh_matrixcontent_menuitem` (`id`, `elementId`, `locale`, `field_product_productName`, `field_product_description`, `field_product_price`, `field_product_extra`, `dateCreated`, `dateUpdated`, `uid`) VALUES
 ('3', '124', 'ru_ru', 'Heineken', 'Heineken (нидерл. Heineken Brouwerijen ) — нидерландская пивоваренная компания, крупнейшая в стране. Штаб-квартира — в Амстердаме.Heineken — самый популярный экспортный пивной бренд компании. В Европе самым продаваемым пивным брендам является Heineken.', '500 мл./ 260 р.', '', '2017-06-05 18:23:31', '2017-08-10 14:26:17', '8381b134-3421-432b-89ad-7b15cc631e76'),
 ('4', '125', 'ru_ru', 'Крушовице светлое или темное', 'Согласно легенде, считается, что Королевская Пивоварня Крушовице была основана аристократом по имени Иржи Бирка в 1517. Таким образом это один из самых старинных пивоваренных заводов в Чешской республике. Первый официальный письменный документ, в котором упоминаются данные про основание завода, датируется 1581 г. В нём указано, что Иржи Бирка предложил королю Рудольфу II купить пивоварню вместе с прилежащими землями. Krušovice Imperial - светлое пиво золотистого оттенка, обладающее восхитительным вкусом с примесью горечи, отличной пеной и приятной резкостью. Пиво варится по классическим методикам. Обладает 12%-ной плотностью и 5%-ным содержанием алкоголя. Krušovice Cerne - темное пиво, которое считается традиционным напитком Королевской пивоварни и варится более 100 лет. Обладает насыщенным карамельным вкусом и имеет легкий хмельной аромат. Содержание алкоголя - 3,8%, плотность - 10%.', '500 мл./ 290 р.', '', '2017-06-05 18:23:31', '2017-08-10 14:26:17', 'ffdaf661-b120-496e-ad4a-99ce830f320c'),
 ('5', '126', 'ru_ru', 'Velka Morava КРАФТОВОЕ светлое или пшеничное', 'Моравский класс светлое — 4,2/11,5 Пиво в пльзеньском стиле. В аромате луговые цветы, немного фруктов и слабым фоном немного ириски. Вкус насыщенный, как говорится — тело плотное. Послевкусие хмелевое, горькое, сухое и очень долгое. Пиво необычайно питкое. Вкус очень сбалансированный, чем-то действительно напоминает Праздрой, но наверное даже более яркий.', '500 мл./ 290 р.', '', '2017-06-05 18:23:31', '2017-08-10 14:26:17', 'c8d84087-608d-4c3a-81d1-550339e1e4c6'),
 ('6', '127', 'ru_ru', 'Jaws American Pale Ale КРАФТОВОЕ разливное', 'Jaws American Pale Ale разливное Россия Выпускается под брендом «Jaws Brewery» Хорошо охмеленное пиво с яркими нотами манго, цитрусов и сосновой хвои. Состав: вода, солод ячменный светлый, хмель, дрожжи. Расчетная горечь - 43 IBU Характеристики: Цвет - Средне-янтарное Крепость, Об - 5.2 Тип ферментации - Верховой Плотность, % - 13.5 Тип фильтрации - Фильтрованное Термическая обработка - Непастеризованное', '500 мл./ 290 р.', '', '2017-06-05 18:23:31', '2017-08-10 14:26:17', 'f3153938-ae0a-466d-ad95-cf79f2c0f742'),
 ('7', '128', 'ru_ru', 'Пяток домашнее светлое', 'С 2002 года для ценителей всего натурального на частной пивоварне \"Пятый океан\" варятся оригинальные сорта пива. Для их приготовления никогда не используются искусственные ароматизаторы и консерванты.', '500 мл./ 190 р.', '', '2017-06-05 18:23:31', '2017-08-10 14:26:17', 'f0ec9f0a-6036-4ba1-b8db-ea98a553b06d'),
 ('9', '131', 'ru_ru', 'Edelweiss спец.', 'Edelweiss Weissbier (также встречается Edelweiß Weißbier) — сорт белого пива, В 1475 году мэр и судья города Зальцбурга Йохан Айзенхаймер основал в маленькой деревеньке Кальтенхаузен, расположенной в самом сердце Альп, у подножья горы Унтерсберг, пивоварню Kalte Brauhaus («Холодный пивоваренный дом»).Kaltenhausen является не только старейшей пивоварней земли Зальцбурга, но и первым производителем нефильтрованного пшеничного пива в Австрии. История пивоварения Kaltenhausen насчитывает более 500 лет, а нефильтрованное пиво здесь начали варить 350 лет назад, намного опередив большинство пивоварен Баварии. В конце прошлого века компания BRAU UNION, возродив многовековые традиции пивоварения, выпустила на рынок нефильтрованное пиво Edelweiss, основным местом производства которого стала пивоварня Kaltenhausen.', '500 мл./ 220 р. спец.', '', '2017-06-05 18:25:50', '2017-08-10 14:31:13', 'f0c39495-fad2-4ab5-a487-0d74e676eb0f'),
 ('10', '132', 'ru_ru', 'Guinness спец.', '«Ги́ннесс» (англ. Guinness) — пивной бренд, принадлежащий компании Diageo (со штаб-квартирой в Лондоне), изначально относившийся к ирландской компании Arthur Guinness Son & Co, основанной в 1759 году пивоваром Артуром Гиннессом (Arthur Guinness). Пиво «Гиннесс» — самое известное и потребляемое ирландское пиво, ставшее легендой Ирландии и символом дня святого Патрика. Отличительной особенностью пива долгое время являлся жжёный аромат, который получался из-за использования жареного ячменя. Пиво производится из ячменя (в Нигерии его заменяют на сорго, воды, хмеля и дрожжей. На протяжении многих лет отстоявшееся пиво смешивали со свежесваренным, что придавало резкий молочный привкус и характерную пену. Позже компания Гиннесса отказалась от смешивания отстоявшегося и свежесваренного пива, и теперь для создания пены при разливе пиво обогащают азотом; кроме того, в качестве основы производства пива компании по лицензии сейчас зачастую служит концентрированный экстракт «FES», который можно разводить на основе лагера. В пинте «Гиннесса» всего 198 килокалорий — меньше, чем в апельсиновом соке или обезжиренном молоке.', '500 мл./ 370 р. спец.', '', '2017-06-05 18:25:50', '2017-08-10 14:31:13', '2cd41ed7-3f20-4b82-8fbc-aface64fd10a'),
